using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.IO;

namespace JpegFromMysqlBlob
{
    class Program
    {
        static void Main(string[] args)
        {
            string testMysqlBlobImage = "";

            byte[] byteArray = StringToByteArray(testMysqlBlobImage);

            using (var imageFile = new FileStream(@"C:\testMysqlBlobImage.jpeg", FileMode.Create))
            {
                imageFile.Write(byteArray, 0, byteArray.Length);
                imageFile.Flush();
            }
        }

        public static byte[] StringToByteArray(string hex)
        {
            return Enumerable.Range(0, hex.Length)
                             .Where(x => x % 2 == 0)
                             .Select(x => Convert.ToByte(hex.Substring(x, 2), 16))
                             .ToArray();
        }
    }
}